     useEffect(()=>{
      axios.get("http://localhost:4000")

      .then((res) => {

          let items=res.data

          products.push(items)
          console.log(products)


       })


      },[])


























  return (


    <div className="App"> 
      {
        products.map((product)=>{
           <button  className="btn btn-primary">Add to cart</button>
        })
      } 





   </div>  







  )

}

export default View
