import React, { useEffect,useState } from 'react'
import axios from 'axios'
function Cart() {
   const[products,setProducts]=useState([])
   const[cart,setCart]=useState({})
   const[user,setUser]=useState('')
   useEffect(()=>{
    axios.get('/cartt').then((res)=>{
       console.log(res)
      setProducts(res.data.products)
      setCart(res.data.total)
      setUser(res.data.user)

  })
  },[])
  function changeQuantity(cartId,productId,userId,count){
    console.log(cartId)
    let quantity=parseInt(document.getElementById(productId).innerHTML)
     axios.post('/change-product',

        {
          user:userId,
          cart:cartId,
          proId:productId,
          count:count,
          quantity:quantity

        })

        .then((response)=>{
          if(response.removeproduct){
          alert("product removed")
          window.reload()
        }else {

          document.getElementById(productId).innerHTML=count+quantity
          document.getElementById("total").innerHTML=response.total

        }
        })
      }




  return (
    <div>
        <table className="table">
  <thead>
    <tr>

      <th scope="col">iteam</th>
      <th scope="col">title</th>
       <th scope="col">Price</th>
      <th scope="col">quantity</th>

    </tr>
  </thead>
  <tbody> 
     {products.map((product)=>{
        return (
     <tr>

      <td><img style={{width:'70px',height:'70px'}} src={'/productimages/'+product.product._id+'.jpg'} alt=""/></td>
      <td>{product.product.name}</td>
      <td>{product.product.Price}</td>

      <td><button class="cart-item-count mr-3" onclick={()=>changeQuantity(cart._id,product._id,user,-1)}>-</button>
      <span id={product.product._id}></span>
      <button class="cart-item-count ml-3"  onclick={()=>changeQuantity(cart._id,1)}>+</button></td>
      <td><button class="btn btn-danger" onclick="remove('{{this._id}}','{{this.product._id}}')">remove</button></td>
    </tr>



        )})}


  </tbody>
</table>
<hr/>
<div class="float-right pr-5">
<h3 class="float-left mr-5">TOTAL:Rs.<span id="total"></span></h3>
<a href="/placeorder/" class="btn btn-success  mt-3" style={{width:'100%'}}><b>placeorder</b></a> 
</div>
</div>
  )
}

export default Cart
